select case (profiles.gender)
    WHEN 'M' THEN 'Man'
    WHEN 'F' THEN 'Woman'
    END AS gender, count(likes.user_id) as count
    from likes, profiles
    where likes.user_id = profiles.user_id
    group by profiles.gender;

